import React, { useState, useEffect } from "react";

import catA from "../../../media/prod-0.png";
import catB from "../../../media/prod-1.png";
import catC from "../../../media/prod-2.png";
import catD from "../../../media/prod-3.png";
import catE from "../../../media/prod-4.png";
import catF from "../../../media/prod-5.png";

const products = [
  { name: "Aa", source: catA },
  { name: "Lavanderia", source: catB },
  { name: "C", source: catC },
  { name: "D", source: catD },
  { name: "C", source: catE },
  { name: "D", source: catF },
];
const ShowCategories = (props) => {
  const [cats, setCats] = useState(products);

  useEffect(() => {
    console.log(products);
  }, []);

  const renderCats = () => {
    return cats.map((item, k) => {
      console.log(item.source);
      return (
        <div className="card" key={k}>
          <img className="card-img-top" src={item.source} alt="Card  cap" />
          <div className="card-body">
            <h5 className="card-title">{item.name}</h5>
          </div>
        </div>
      );
    });
  };

  return (
    <React.Fragment>
      <div className="mr-auto ml-auto card-group col-10">{renderCats()}</div>
    </React.Fragment>
  );
};

export default ShowCategories;
