import React, { useState, useEffect } from "react";

import catA from '../../../media/prod-0.png'

const products = [
  { name: "Aa", source: "../../../media/prod-0.png" },
  { name: "B", source: "https://dummyimage.com/300x300/991d99/fff" },
  { name: "C", source: "https://dummyimage.com/300x300/000/fff" },
  { name: "D", source: "https://dummyimage.com/300x300/000/fff" },
];
const ShowCategories = (props) => {
  const [cats, setCats] = useState(products);

  useEffect(() => {
    console.log(products);
  }, []);

  const renderCats = () => {
    return cats.map((item, k) => {
      console.log(item);
      return (
        <div className="card" key={k}>
          <img
            className="card-img-top"
            src="https://dummyimage.com/300x300/000/fff"
            alt="Card  cap"
          />
          <div className="card-body">
            <h5 className="card-title">{item.name}</h5>
          </div>
        </div>
      );
    });
  };

  return (
    <React.Fragment>
      <div className="mr-auto ml-auto card-group col-10">{renderCats()}</div>
    </React.Fragment>
  );
};

export default ShowCategories;
