import React, { useState } from "react";

const AllImages = (props) => {
  const bg0 = "../../../media/bg-0.jpg";
  const bg1 = "../../../media/bg-1.jpg";
  const [images, setImages] = useState([bg0, bg1]);

  const renderImages = () => {
    console.warn(`render ${images.length} Images`);
    if (images) {
      return images.map((image, k) => {
        console.info(`k = ${k}`);
        return (
          <div className="image-card-container" key={k}>
            <div key={image.imageId} className="image-card">
              <h4 className="image-title">{image.title}</h4>
              <img
                className="main-image"
                src={image.image}
                alt="This is a terrible description!"
              />
            </div>
          </div>
        );
      });
    }
  };

  return(
      <React.Fragment>
      <div style={{ border: "1px dashed red" }}>
        {images.length !== 0 ? renderImages() : ""}
      </div>
    </React.Fragment>
  )

};

export default AllImages;
