import React, { useState } from "react";
import moment from "moment";

const blogEntry = [
  {
    title: "Blog entry title",
    resumen: "",
    text: "",
    image: "https://dummyimage.com/600x400/000/fff",
    hasHighlight: true,
    highlight: "",
    date: "20200202",
  },
];

const ShowBlog = (props) => {
  const [dateBlog, setDateBlog] = useState();

  const showBlogCars = () => {
    blogEntry.map((item, k) => {
      return (
        <div className="card">
          <img className="card-img-top" src={item.image} alt="Card imge cap" />
          <div className="card-body">
            <h5 className="card-title">{item.title}</h5>
            <p className="card-text">{item.resumen}</p>
            <p className="card-text">
              <small className="text-muted">
                {moment(item.date).startOf("day").fromNow()}
              </small>
            </p>
          </div>
        </div>
      );
    });
  };

  return (
    <React.Fragment>
      <div className="mr-auto ml-auto card-group col-11">
        <div className="card">
          <img
            className="card-img-top"
            src="https://dummyimage.com/600x400/000/fff"
            alt="Card  cap"
          />
          <div className="card-body">
            <h5 className="card-title">Card title</h5>
            <p className="card-text">
              This is a wider card with supporting text below as a natural
              lead-in to additional content. This content is a little bit
              longer.
            </p>
            <p className="card-text">
              <small className="text-muted">
                {" "}
                {moment("20200612").startOf("day").fromNow()}
              </small>
            </p>
          </div>
        </div>
        <div className="card">
          <img
            className="card-img-top"
            src="https://dummyimage.com/600x400/000/fff"
            alt="Card  cap"
          />
          <div className="card-body">
            <h5 className="card-title">Card title</h5>
            <p className="card-text">
              This card has supporting text below as a natural lead-in to
              additional content.
            </p>
            <p className="card-text">
              <small className="text-muted">
                {" "}
                {moment("20200609").startOf("day").fromNow()}
              </small>
            </p>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default ShowBlog;
