// import React, { Component } from "react";
import React, { useState } from "react";
import { NavLink as NabLink } from "react-router-dom";
import { Collapse, Navbar, NavbarToggler, Nav } from "reactstrap";
import "./Navbar.css";

const NavBar = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [isLogged, setIsLogged] = useState(false);
  const [userName, setUserName] = useState();

  const toggle = () => setIsOpen(!isOpen);


  return (
    <div className="mb-2 pb-2">
      <Navbar
        color="light"
        light
        // expand="xs"
        fixed="top"
        style={{ boxShadow: "2px 1px 2px black" }}
      >
        <NabLink
          className="navThing"
          exact
          to={"/"}
          activeClassName="activeNavLink"
        >
          <span role="img" aria-label="star-dust">
            {" "}
            ðŸš€
          </span>
          Academo
        </NabLink>

        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="mr-auto" navbar>
            <NabLink
              className="navThing link-text"
              activeClassName="activeNavLink"
              to="/dashboard"
            >
              Cursos
            </NabLink>
          </Nav>
          {isLogged ? (
            <React.Fragment>
              <NabLink
                className="navThing link-text user-text"
                to={"/notas"}
                activeClassName="activeNavLink"
              >
                <span className="blogoutUser order-success p-1">
                  {userName.split(" ")[0]}
                </span>
              </NabLink>
            </React.Fragment>
          ) : (
            <React.Fragment>
              <NabLink
                className="navThing link-text"
                to={"/register"}
                activeClassName="activeNavLink"
              >
                <span className="blogoutUser order-success p-1">registro</span>
              </NabLink>
              <NabLink
                className="navThing link-text"
                to={"/login"}
                activeClassName="activeNavLink"
              >
                <span className="p-1">login </span>
              </NabLink>
            </React.Fragment>
          )}
        </Collapse>
      </Navbar>
    </div>
  );
};

export default NavBar;
