import React, { useState, useEffect } from "react";
import moment from "moment";
import { useHistory } from "react-router-dom";

const blogEntry = [
  {
    title: "Blog entry title",
    resumen: "",
    text: "",
    image: "https://dummyimage.com/600x400/000/fff",
    hasHighlight: true,
    highlight: "",
    date: "20200202",
  },
];

const ShowBlog = (props) => {
  const [dateBlog, setDateBlog] = useState();

  const [cats, setCats] = useState(blogEntry);
  const history = useHistory();

  useEffect(() => {
    console.log(blogEntry);
  }, []);

  const showBlogCars = () => {
    blogEntry.map((item, k) => {
      return (
        <div className="card">
          <img className="card-img-top" src={item.image} alt="Card imge cap" />
          <div className="card-body">
            <h5 className="card-title">{item.title}</h5>
            <p className="card-text">{item.resumen}</p>
            <p className="card-text">
              <small className="text-muted">
                {moment(item.date).startOf("day").fromNow()}
              </small>
            </p>
          </div>
        </div>
      );
    });
  };

  return (
    <React.Fragment>
      <div className="mr-auto ml-auto card-group col-11">
      {showBlogCars()}
      </div>
    </React.Fragment>
  );
};

export default ShowBlog;
